(1367, 'en', 'default', '759e3687dbefb5670eb444bb69549e84', 'To edit an existing menu, <strong>choose a menu from the drop down and click Select</strong>', NULL, 'LINE', NULL, 0, '', '90ef2d63481fd79ab0c09c14d93bc812'),
(1368, 'en', 'default', 'c5ecefc2c05db6570556c7385ac2e8a3', 'If you haven&#8217;t yet created any menus, <strong>click the &#8217;create a new menu&#8217; link</strong> to get started', NULL, 'LINE', NULL, 0, '', 'e3750bb5eb2eb05b624dbab6c20b5e59'),
(1369, 'en', 'default', '72caeed1ff140d706e86977a0bf7d12c', 'You can assign theme locations to individual menus by <strong>selecting the desired settings</strong> at the bottom of the menu editor. To assign menus to all theme locations at once, <strong>visit the Manage Locations tab</strong> at the top of the screen.', NULL, 'LINE', NULL, 0, '', '9362d167fdacec13de259ffccbd99596'),
