(1371, 'en', 'default', '61a1fba5abfdef97566c7b73d4ebf8b7', 'Each custom menu may contain a mix of links to pages, categories, custom URLs or other content types. Menu links are added by selecting items from the expanding boxes in the left-hand column below.', NULL, 'LINE', NULL, 0, '', 'a98ddac16b17739ea2c8f12b56b26e5d'),
(1372, 'en', 'default', '2bc2b92ef21e54b7d57719af3e03b906', '<strong>Clicking the arrow to the right of any menu item</strong> in the editor will reveal a standard group of settings. Additional settings such as link target, CSS classes, link relationships, and link descriptions can be enabled and disabled via the Screen Options tab.', NULL, 'LINE', NULL, 0, '', '44adfce5db156b9d67e7c275453a89b9'),
