(1913, 'en', 'contact-form-7', 'b4aa19ec94d1742f74a766499938086b', 'Getting spammed? You have protection.', NULL, 'LINE', NULL, 0, '', '0b63c1e598c840e1f12b81dd7834d61f'),
(1914, 'en', 'contact-form-7', 'dfc3c1985dbc3500051df269ac496f38', 'Spammers target everything; your contact forms aren&#8217;t an exception. Before you get spammed, protect your contact forms with the powerful anti-spam features Contact Form 7 provides.', NULL, 'LINE', NULL, 0, '', '449ce02dc0fa8fdd5c88d4b77e37585c'),
