(1922, 'en', 'contact-form-7', '0806da1b0eb97e00197c070038c16ac7', 'Contact Form 7 doesn&#8217;t store submitted messages anywhere. Therefore, you may lose important messages forever if your mail server has issues or you make a mistake in mail configuration.', NULL, 'LINE', NULL, 0, '', '79ea1b317a3594529ad43dc8e5238f9d'),
(1923, 'en', 'contact-form-7', '634711044793d2163bb96136d63b5fe2', 'Install a message storage plugin before this happens to you. %s saves all messages through contact forms into the database. Flamingo is a free WordPress plugin created by the same author as Contact Form 7.', NULL, 'LINE', NULL, 0, '', '886327860e62fd202d832b9d620f109a'),
