INSERT INTO `wp_icl_strings` (`id`, `language`, `context`, `name`, `value`, `string_package_id`, `type`, `title`, `status`, `gettext_context`, `domain_name_context_md5`) VALUES
(3350, 'en', 'default', '16fd34c3de2b02f60c7813d5965faecb', '<strong>WordPress</strong> &mdash; Updating your WordPress installation is a simple one-click procedure: just <strong>click on the &#8220;Update Now&#8221; button</strong> when you are notified that a new version is available.', NULL, 'LINE', NULL, 0, '', 'a2c5ffc4b69c8d049bce519b8013c8cb'),
(3351, 'en', 'default', '72247007ca8dd9d5b747cd2d13855ae4', 'In most cases, WordPress will automatically apply maintenance and security updates in the background for you.', NULL, 'LINE', NULL, 0, '', 'f608b0ae3b2099cd13950879df8872a3'),
(3352, 'en', 'default', '83898cec1dbe4f4586c8347c1ba46120', '<strong>Themes and Plugins</strong> &mdash; To update individual themes or plugins from this screen, use the checkboxes to make your selection, then <strong>click on the appropriate &#8220;Update&#8221; button</strong>. To update all of your themes or plugins at once, you can check the box at the top of the section to select all before clicking the update button.', NULL, 'LINE', NULL, 0, '', '98ac0766c2d6280e0f1ccde117bde100'),
(3353, 'en', 'default', 'da5abbe81c1b1941c214b1b4298822cb', '<strong>Translations</strong> &mdash; The files translating WordPress into your language are updated for you whenever any other updates occur. But if these files are out of date, you can <strong>click the &#8220;Update Translations&#8221;</strong> button.', NULL, 'LINE', NULL, 0, '', 'd5ff3112c75a90a53ba4a28d043eb4a7'),
(3354, 'en', 'default', 'c6701576b0781a015b3e4de713ec1078', 'How to Update', NULL, 'LINE', NULL, 0, '', 'a9e00d6f73298647183d6dad93001f27'),
(3355, 'en', 'default', '3ca7a638d575621b115fdfc88b3b002b', '<a href=\"https://codex.wordpress.org/Dashboard_Updates_Screen\">Documentation on Updating WordPress</a>', NULL, 'LINE', NULL, 0, '', 'acea509fdbe89d522e67669f655eddf9'),
(3356, 'en', 'default', 'b9f0488fd6301cdc26b8088675bd89cb', 'Last checked on %1$s at %2$s.', NULL, 'LINE', NULL, 0, '', '7f17ae38b21b0110353ff13011ece282'),
